<script lang="tsx" setup>

definePageMeta({
  layout: "default",
  auth: {
    unauthenticatedOnly: true,
    // login to
    navigateAuthenticatedTo: '/',
  },
})

// import TableData from '@/components/tables/table.vue';

import type { FunctionalComponent } from 'vue'
import type { CheckboxValueType, Column } from 'element-plus'


const config = useRuntimeConfig();
const { apiTruyen, apiAuth } = config.public;
const { data: cates, error: errorCate } = await useFetch(apiTruyen + '/cate/all');

type SelectionCellProps = {
  value: boolean
  intermediate?: boolean
  onChange: (value: CheckboxValueType) => void
}

const SelectionCell: FunctionalComponent<SelectionCellProps> = ({
  value,
  intermediate = false,
  onChange,
}) => {
  return (
    <ElCheckbox
      onChange={onChange}
      modelValue={value}
      indeterminate={intermediate}
    />
  )
}

const generateColumns = (length = 10, prefix = 'column-', props?: any) =>
  Array.from([
    // {
    //   key: "id",
    //   dataKey: "id",
    //   title: "id",
    //   width: 350
    // },
    {
      key: 'name',
      dataKey: 'name',
      title: 'name',
      width: 350
    },
    {
      key: 'slug',
      dataKey: 'slug',
      title: 'Slug thể loại',
      width: 300
    },
    {
      key: 'operations',
      title: 'Operations',
      cellRenderer: () => (
        <>
          <ElButton size="small">Edit</ElButton>

        </>
      ),
      width: 150,
      align: 'center',
    }
  ]).map((_item, columnIndex) => ({
    ...props,
    key: _item.key,
    dataKey: _item.dataKey,
    title: _item.title,
    width: _item.width,
  }))

const columns: Column<any>[] = generateColumns(10)
columns.unshift({
  key: 'selection',
  width: 50,
  cellRenderer: ({ rowData }) => {
    const onChange = (value: CheckboxValueType) => (rowData.checked = value)
    return <SelectionCell value={rowData.checked} onChange={onChange} />
  },

  headerCellRenderer: () => {
    const _data = unref(data)
    const onChange = (value: CheckboxValueType) =>
    (data.value = _data.map((row) => {
      row.checked = value
      return row
    }))
    const allSelected = _data.every((row) => row.checked)
    const containsChecked = _data.some((row) => row.checked)

    return (
      <SelectionCell
        value={allSelected}
        intermediate={containsChecked && !allSelected}
        onChange={onChange}
      />
    )
  },
})

const data = computed(() => {
  return cates.value.map((item, rowIndex) => {
    const rowData = {
      id: `${rowIndex}`,
      checked: false,
      parentId: null,
    };

    for (const column of columns) {
      rowData[column.dataKey] = item[column.key];
    }

    return rowData;
  });
});
</script>